import './App.css';
import {React, useEffect} from 'react';
import { browserName, browserVersion } from "react-device-detect";
// import VideoCanvas from './VideoCanvas';
import MyComponent from './MyComponent'
// import DetectCanvas from './DetectCanvas';
import Example from './example';
import Utils from './Utils';

import { OpenCvProvider, useOpenCv } from 'opencv-react'




function LoadOpenCv() {
  const { loaded, cv } = useOpenCv()
  const cv_status = document.getElementById("status");
  let utils = new Utils();
  let classifier;

  useEffect(() => {
    if (cv) {
      console.log(loaded, cv);
      cv_status.style.display = "none";

      let faceCascadeFile = "haarcascade_frontalface_default.xml";
      classifier = new cv.CascadeClassifier();

      utils.createFileFromUrl(faceCascadeFile, faceCascadeFile, () => {
          classifier.load(faceCascadeFile) // in the callback, load the cascade from file
          .then(()=>{
              console.log('AAA'); 
              const ThemeContext = React.createContext(themes.light);
          })
          .catch((err)=>{console.log('load classifier:', err)});

      });



    }
  }, [cv])
  return (<p id="status" style={{display: 'block'}, {fontSize: 20}}>OpenCV.js is loading...</p>)


  // return <p>OpenCv React test</p>
}

const App = () => {

  useEffect(()=>{
    console.log(`${browserName} ${browserVersion}`);
    console.log(typeof(browserName));
    console.log(browserName);
    if (browserName == 'Safari'){
      console.log("yes");
      while(true)
        alert("You use Safari now, please change another browser because Safari not support capture window function");
    }
  })

  return (
    <div> 
    <OpenCvProvider >
      <LoadOpenCv />
      <MyComponent />
      <Example />
      {/*<VideoCanvas />
      <DetectCanvas /> */}
    </OpenCvProvider>
    </div>
  )
}

export default App;
