import { OpenCvProvider, useOpenCv } from 'opencv-react'
import {useEffect, useState} from 'react'
import './MyComponent.css';
import Utils from './Utils';

function MyComponent() {

    const { loaded, cv } = useOpenCv();

    const add_button = document.getElementById("add");
    const zoom_window_text = document.getElementById("zoom_window_text");
    const process_window_text = document.getElementById("process_window_text");
    const video = document.getElementById("cam_input");
    const output = document.getElementById("canvas_output");

    let utils = new Utils();

    // const img_now = new Image();
    // let utils = new Utils();
    // if (video.srcObject!= null)
    // temCtx.drawImage(video, 0, 0);

    const [count, setCount] = useState(0);
    const [videoLoaded, setVideoLoaded] = useState(false);

    const catchZoomWindow = () => {
        navigator.mediaDevices.getDisplayMedia({ video: true, audio: false })
        .then(function(stream) {
            // let video = document.getElementById("cam_input");
            // console.log(video);
            // console.log(stream);
            video.srcObject = stream;
            let settings = stream.getVideoTracks()[0].getSettings();
            console.log(settings);
            add_button.style.display = "none";
            zoom_window_text.style.display = "none";
            process_window_text.style.display = "none";
            // FPS = settings.frameRate;
            output.style.width = video.style.width;
            output.style.height = video.style.height;
            video.play();
        })
        .then(()=>{
            console.log('component catch', cv);
            // console.log(OpenCvProvider.onLoaded);

        })
        .then(()=> {
            openCvReady();
            // utils.loadOpenCv(openCvReady);

        })
        .catch((err) =>{
            console.log("An error occurred! " + err);
        });

    }

    function openCvReady() {
        // const { loaded, cv } = useOpenCv();

        console.log('opencvReady', cv.Mat, loaded);

        let FPS = 0.25;
        // let video = document.getElementById("cam_input");
        let src = new cv.Mat(video.height, video.width, cv.CV_8UC4);
        let dst = new cv.Mat(video.height, video.width, cv.CV_8UC1);
